INSERT INTO `tb_news` VALUES ('25', '08', '计算机工程系2018国家奖助学金推荐名单公示', '计算机工程系2018国家奖助学金推荐名单公示', 'admin', '<span style=\"font-family:宋体;font-size:16pt;\">按国家奖助学金评选条件，根据班测评小组推荐排名，经系测评小组审核，现将系推荐名单公示如下：<span></span></span> \r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:16pt;\"></span>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">国家奖学金</span></b><span style=\"font-family:仿宋_GB2312;font-size:16pt;\"></span>\r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">12</span></b><b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">移动通信班：</span></b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">刘冬青<b><span></span></b></span>\r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">12</span></b><b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">平面设计班：</span></b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">梁洁敏<span></span></span>\r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\"></span></b>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">国家励志奖学金<span></span></span></b>\r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">12</span></b><b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">软件应用<span>1</span>班：</span></b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">黎彤彤、钟丽如</span><span style=\"font-size:14pt;\"></span>\r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"font-family:仿宋_GB2312;font-size:14pt;\">12</span></b><b><span style=\"font-family:仿宋_GB2312;fo