INSERT INTO `tb_news` VALUES ('31', '02', '热烈祝贺学院教务处网站成功改版', '热烈祝贺学院教务处网站成功改版', 'admin', '&nbsp;<span style=\"line-height:125%;font-family:宋体;font-size:12pt;\">经过学院教务处、计算机工程系、实训中心这</span><span style=\"line-height:125%;font-size:12pt;\">三</span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;\">部门老师的共同努力，学院教务处网页成功改版，界面清爽、大方、简洁。给人能留下美好的印象。希望计算机工程系能继续努力，充分利用计算机专业知识，为学院的数字化信息建设添砖加瓦，为其他系部提供更优质的网络信息服务。</span>', '1536869822', '1');
