INSERT INTO `tb_news` VALUES ('50', '09', '迎新报道二：金秋送爽喜迎八方学子', '迎新报道二：金秋送爽喜迎八方学子', 'admin', '<p style=\"line-height: 47px; text-indent: 40px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">金秋送爽</span><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">喜迎八方学子</span><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">，</span><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\"> 9月16日，是</span><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">我院2017级新同学入学报到的第一天，迎着清晨的朝霞，水东湾新城校区、文明北校区及火车站、汽车站的迎新接待站的迎新工作一大早已经忙碌起来，大家忙得不亦乐乎。</span></span></p><p style=\"line-height: 47px; text-indent: 40px;\"><span style=\"color: black; font-family: 仿宋_GB2312; font-size: 20px;\">上午，学院党委书记高鹏，院长张庆，党委副书记罗卫东，副院长梁德萍，副院长、纪委书记谈毅分别在水东湾新城校区与文明北校区指导迎新工作。学院领导亲临火车站、汽车站慰问迎新工作人员和志愿者，慰问了校园里正在为迎新工作忙碌的大二学子，慰问了现场开展校企合作的企业代表，为他们的热情工作表示衷心感谢，现场与新生、家长亲切交流，并勉励新同学，好好学习，争取早日成才。</span></p><p style=\"line-height: 47px; text-indent: 40px;\"><span style=\"color: black; font-family: 仿宋_GB2312; font-size: 20px;\">为确保新生顺利办理各项手续，学院采取一站式服务，新生报到确认、校园</span><span style=\"color: black; font-family: 仿宋_GB2312; font-size: 20px;\">卡领取及使用说明、缴费情况确认和住宿通知单领取，一站式服务简化了入学手续，为广大新生报到带来了极大的便利。迎新工作处处彰显了人性化的特点，大到整个迎新氛围的营造，小至导航标志的设置，安排详尽，管理精细，实施规范，尽力做到“人性化、科学化、规范化，更贴心、更周到、更有序”,使得离家求学的2017级新生备感学校的温暖，让家长们放心，让学生舒心。</span></p><p style=\"line-height: 47px; text-indent: 40px;\"><span style=\"color: black; font-family: 仿宋_GB2312; font-size: 20px;\"><img title=\"1474899809109580.jpg\" alt=\"20170916034120-2779359044875073694.jpg\" src=\"/ueditor/php/upload/image/20170926/1474899809109580.jpg\"/></span></p><p style=\"line-height: 47px; text-indent: 40px;\"><span style=\"color: black; font-family: 仿宋_GB2312; font-size: 20px;\"><img title=\"1474899816497060.jpg\" alt=\"20170916034133-440962628542130576.jpg\" src=\"/ueditor/php/upload/image/20170926/1474899816497060.jpg\"/></span></p><p>&nbsp;</p><p>			\r\n			</p>', '1536869822', '1');
